import React, {useState, useContext} from 'react'
import { View,  StyleSheet } from 'react-native';
import { Button, Input, Image } from "react-native-elements";
import { StatusBar } from "expo-status-bar";
import { KeyboardAvoidingView } from 'react-native';
import {TouchableOpacity} from 'react-native-gesture-handler'
import styled from "styled-components"

import {FirebaseContext} from '../context/FirebaseContext';
import {UserContext} from '../context/UserContext';
import Text from '../components/Text'

export default  SignUpScreen = ({navigation}) => {
    const [username, setUsername] = useState();
    const [email, setEmail] = useState();
    const [password, setPassword] = useState();
    const [height, setHeight] = useState();

    const [loading, setLoading] = useState();
    const firebase = useContext(FirebaseContext)
    const [_, setUser] = useContext(UserContext)

    const signUp = async () => {
        setLoading(true)

        const user = {username, email, password, height}
        try{
            const createdUser = await firebase.createUser(user)
            setUser({...createdUser, isLoggedIn: true})

        }catch(error){
            console.log("Error @signup: ", error)
        }finally{
            setLoading(false);
        }
    }

    return (

        <KeyboardAvoidingView behavior="padding" style={styles.container}>
        <StatusBar style="dark"/>
        <Image
            source={  
                require('../../assets/Logo-red.png')
            }
            style={{ width:138, height:200 }}
        />
                <View style={{height: 10 }}/>

        <View style={styles.inputContainer}>
                    <View style={{height: 20 }}/>

                <Input
                    placeholder="Username"
                    autoFocus
                    type="text"
                    value={username}
                    onChangeText={(username) => setUsername(username.trim())}
                />

                <Input
                    placeholder="Email"
                    type="email"
                    value={email}
                    onChangeText={(email) => setEmail(email.trim())}
                />

                <Input
                    placeholder="Password"
                    secureTextEntry
                    type="password"
                    value={password}
                    onChangeText={(password) => setPassword(password.trim())}
                />

              </View>


        <TouchableOpacity onPress={signUp}
            style={styles.button}
        >       
            <Text style={{ color: "white", fontSize: 17 }}> Registrati </Text>
        </TouchableOpacity>  

        <Signin onPress={() => navigation.navigate("SignIn")}>
            <Text small center>
                Hai già un account? <Text bold>Accedi</Text>
            </Text>
        </Signin>

        <View style={{height: 100 }}/>

    </KeyboardAvoidingView>


    );
}
const Signin = styled.TouchableOpacity`
    margin-top: 16px;

`


const styles = StyleSheet.create({
    container:{
        flex: 1,
        alignItems: "center",
        justifyContent: "center",
        padding: 10,
        backgroundColor: "#E5E1EE",
    },
    inputContainer: {
        width: 300,
    },
    button: {
        alignItems: 'center',
        backgroundColor: '#DB504A',
        padding: 10,
        borderRadius:18,
        width: 140,
        height: 40,

      },
      button2: {
        alignItems: 'center',
        backgroundColor: '#429EB3',
        padding: 10,
        borderRadius:18,
        width: 140,
        height: 40,

      }
});
