import React, {useState, useContext} from 'react'
import { View, StyleSheet } from 'react-native';
import { Button, Input, Image } from "react-native-elements";
import { StatusBar } from "expo-status-bar";
import { KeyboardAvoidingView } from 'react-native';
import {TouchableOpacity} from 'react-native-gesture-handler'
import styled from "styled-components"

import {FirebaseContext} from "../context/FirebaseContext"
import {UserContext} from "../context/UserContext"
import Text from '../components/Text'

export default  SignInScreen = ({navigation}) => {
    const [email, setEmail] = useState();
    const [password, setPassword] = useState();
    const [loading, setLoading] = useState();
    const firebase= useContext(FirebaseContext)
    const [_, setUser] = useContext(UserContext)

    const signIn= async () => {
            setLoading(true)
            try{
                await firebase.signIn(email,password)
                const uid= firebase.getCurrentUser().uid
                const userInfo = await firebase.getUserInfo(uid)
                setUser({
                    username: userInfo.username,
                    email: userInfo.email,
                    uid,
                    isLoggedIn:true,
                });
            }catch (error) {
                alert(error.message);
            }finally{
                setLoading(false);
            }
    }
    return (

        <View  style={styles.container}>
        <StatusBar style="dark"/>
        <Image
            source={  
                require('../../assets/Logo-red.png')
            }
            style={{ width:138, height:200 }}
        />
        <View style={{height: 20 }}/>
        <Text title semi center>Benvenuto</Text>
        <View style={{height: 20 }}/>

        <View style={styles.inputContainer}>
            <Input 
            placeholder ="Email" 
            autoFocus 
            type="Email" 
            onChangeText= {email => setEmail(email.trim())}
            value={email}
            />

            <Input 
            placeholder ="Password" 
            secureTextEntry 
            type="Password" 
            onChangeText= {password => setPassword(password.trim())}
            value={password}
            />
            </View>


        <TouchableOpacity
            onPress={signIn}
            style={styles.button}
        >       
            <Text style={{ color: "white", fontSize: 17 }}> Login </Text>
        </TouchableOpacity>  

        <Signup onPress={() => navigation.navigate("SignUp")}>
            <Text small center>
                Non sei ancora registrato? <Text bold>Sign</Text>
            </Text>
        </Signup>

        <View style={{height: 200 }}/>
    </View>


    );
}

const Signup = styled.TouchableOpacity`
    margin-top: 16px;

`
const styles = StyleSheet.create({
    container:{
        flex: 1,
        alignItems: "center",
        justifyContent: "center",
        padding: 10,
        backgroundColor: "#E5E1EE",
    },
    inputContainer:Â {
        width: 300,
    },
    button: {
        alignItems: 'center',
        backgroundColor: '#F15152',
        padding: 10,
        borderRadius:18,
        width: 140,
        height: 40,

      },
      button2: {
        alignItems: 'center',
        backgroundColor: '#429EB3',
        padding: 10,
        borderRadius:18,
        width: 140,
        height: 40,

      }
});
