import React from 'react'
import {createBottomTabNavigator} from "@react-navigation/bottom-tabs"
import {Ionicons} from "@expo/vector-icons"

import ExercisesScreen from "../screens/ExercisesScreen"
import MeasurementsScreen from "../screens/MeasurementsScreen"
import ProfileScreen from "../screens/ProfileScreen"
import ProgressesScreen from "../screens/ProgressesScreen"

import {MaterialCommunityIcons} from '@expo/vector-icons'

export default MainStackScreens = () => {
    const MainStack = createBottomTabNavigator()

    const tabBarOptions ={
        showLabel : false,
        style: {
            backgroundColor: "#34344A",
            paddingBottom: 12,
        }
    }

    const screenOptions = (({route}) => ({
        tabBarIcon: ({focused}) => {
            let iconName= "dumbbell";
            switch (route.name) {
                case "Exercises":
                    iconName = "dumbbell";
                    break;
                case "Measurements":
                    iconName = "tape-measure";
                    break;
                case "Progresses":
                    iconName = "chart-areaspline";
                    break;
                case "Profile":
                    iconName = "account";
                    break;

                default: 
                    iconName= "dumbbell"
            }
            if(route.name === "Workout") {
                return (
                    <MaterialCommunityIcons 
                    name="play-circle" 
                    size={48} 
                    color={focused ? "#F15152" : "#ffffff" }  
                    style={{shadowColor: "#34344A", shadowOffset: { width: 0, height: 10}, shadowRadius: 10, shadowOpacity: 0.3 }}
                    />
                )
            }

            return <MaterialCommunityIcons name = {iconName} size={24} color={focused ? "#F15152" : "#6666ffffffffffff66" } />;
        }

    }))
    return(
        <MainStack.Navigator tabBarOptions={tabBarOptions} screenOptions={screenOptions}>
            <MainStack.Screen name="Exercises" component={ExercisesScreen} />
            <MainStack.Screen name="Measurements" component={MeasurementsScreen} />
            <MainStack.Screen name="Workout" component={MeasurementsScreen} />
            <MainStack.Screen name="Progresses" component={ProgressesScreen} />
            <MainStack.Screen name="Profile" component={ProfileScreen} />
        </MainStack.Navigator>
    )
}
